import { useSelector } from "react-redux";

const ProductComponent = () => {
  const products = useSelector((state) => state.allProducts.products);

  const renderList = products.map((product) => {
    const { id, title, image, price, category } = product;
    return (
      <div key={id} className="w-full h-100 sm:w-1/2 md:w-1/4 lg:w-1/4 xl:w-1/4">
        <div className="rounded overflow-hidden shadow-lg m-4">
          <img className="w-full h-40 object-contain" src={image} alt={title} />
          </div>
          <div className="px-2 pt-4 pb-2">
          <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{title}</div>
          </div>
            <div className="meta price">$ {price}</div>
            <div className="meta">{category}</div>
          </div>
        </div>
    );
  });

  return <div className="flex flex-wrap justify-center gap-2">{renderList}</div>;
};

export default ProductComponent;
