import { useSelector } from "react-redux";

const ProductComponent = () =>{
    const products = useSelector((state) => state.allProducts.products);
    console.log("porduct compents data")
    console.log(products)
    const renderList = products.map((product) => {
      const { id, title, image, price, category } = product;
      return (
        <div className="four w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/4 p-4" key={id}>
    <Link to={`/product/${id}`} className="block">
        <div className="rounded overflow-hidden shadow-lg">
        <div className="relative pb-2/3">
            <img className="absolute h-full w-full object-cover" src={image} alt={title} />
        </div>
        <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{title}</div>
        </div>
        <div className="px-6 pt-4 pb-2">
            <div className="header">{title}</div>
            <div className="meta price">$ {price}</div>
            <div className="meta">{category}</div>
        </div>
        </div>
    </Link>
    </div>
      );
    });

    return<div className="flex flex-wrap justify-center">{renderList}</div>;
}
export default ProductComponent;
